async function submitForm(e){e.preventDefault();const t={name:document.getElementById("name").value,email:document.getElementById("email").value,contactNo:document.getElementById("contactNo").value,message:document.getElementById("message").value},n=await fetch("https://apis.byrohan.in/v1/contact",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(t)}),o=await n.text();document.querySelector(".TitleBar").innerText=`${o}`}function clearForm(){document.getElementById("form").reset();document.querySelector(".TitleBar").innerText=""}window.addEventListener("scroll",function(){const e=document.querySelector(".menu");window.innerHeight+window.scrollY>=document.documentElement.scrollHeight?e.classList.add("hidden"):e.classList.remove("hidden")});