async function submitForm(e){e.preventDefault();e={name:document.getElementById("name").value,email:document.getElementById("email").value,contactNo:document.getElementById("contactNo").value,message:document.getElementById("message").value},e=await(await fetch("https://api.rohandev.online/contact",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify(e)})).text();document.querySelector(".TitleBar").innerText=""+e}function clearForm(){document.getElementById("form").reset(),document.querySelector(".TitleBar").innerText=""}window.addEventListener("scroll",function(){var e=document.querySelector(".menu"),t=window.innerHeight+window.scrollY;document.documentElement.scrollHeight<=t?e.classList.add("hidden"):e.classList.remove("hidden")});